package org.raosantosh.algorithms.leetcode.string2;

/**
 * Created by s.rao on 4/5/18. Given a string S, you are allowed to convert it to a palindrome by adding characters in
 * front of it. Find and return the shortest palindrome you can find by performing this transformation.
 *
 * For example:
 *
 * Given "aacecaaa", return "aaacecaaa".
 *
 * Given "abcd", return "dcbabcd".
 */
public class ShortestPalindrome {

  public String shortestPalindrome(String s) {
    String shortestString = "";

    char[] stringChars = new char[s.length()];
    new StringBuffer(s).getChars(0, s.length(), stringChars,0 );

    for(int i=stringChars.length-1; i >= 0; i--) {

      if(isPaliindrome(stringChars,0,i)) {
        String prefix = s.substring(0, i+1);
        String suffix = s.substring(i+1);
        return (new StringBuffer(suffix)).reverse() + prefix + suffix;
      }
    }

    return shortestString;
  }

  private boolean isPaliindrome(char[] stringChars, int start, int end ) {
    while(start < end) {
      if(stringChars[start++] != stringChars[end--])
        return false;
    }
    return true;
  }

  public String shortestPalindrome1(String s) {
    boolean [][] isPalindrome = new boolean[s.length()][s.length()];
    int maxPrefix = 0;

    if(s.isEmpty() || s.length() == 1) return s;

    for(int i= 0; i < isPalindrome.length; ++i) {
      isPalindrome[i][i] = true;
      if((i+1) < s.length() && s.charAt(i) == s.charAt(i+1) ) {
        isPalindrome[i][i+1] = true;
        isPalindrome[i+1][i] = true;
        if(i == 0)
          maxPrefix = 1;
      }
    }

    for(int length =2; length < s.length(); ++length) {
      for(int startIndex = 0; startIndex < s.length() -length; ++startIndex) {
        if(s.charAt(startIndex) == s.charAt(startIndex + length)) {
          isPalindrome[startIndex][startIndex+length] = isPalindrome[startIndex+1][startIndex + length -1];
          if(startIndex == 0 && isPalindrome[startIndex][startIndex+length]) {
            maxPrefix = length;
          }
        }
      }
    }

    return (new StringBuffer(s.substring(maxPrefix+1, s.length()))).reverse().append(s).toString();
  }

  private boolean isPaliindrome(String s) {

    int start = 0;
    int end = s.length() - 1;
    while(start < end) {
      if(s.charAt(start++) != s.charAt(end--))
        return false;
    }
    return true;
  }

  public static void main(String args[]) {
    ShortestPalindrome finder = new ShortestPalindrome();
    System.out.println(finder.shortestPalindrome("aacecaaa"));
    System.out.println(finder.shortestPalindrome("abcd"));
    System.out.println(finder.shortestPalindrome("aaa"));
    System.out.println(finder.shortestPalindrome("abcde"));
    System.out.println(finder.shortestPalindrome(""));
    System.out.println(finder.shortestPalindrome("a"));
    System.out.println(finder.shortestPalindrome("aba"));

    System.out.println(finder.shortestPalindrome(""));
  }
}
